export class BadRequest {
  readonly status: 400 = 400;
  constructor(message: string) {
    throw {message, status: this.status};
  }
}

export class NoContent {
  readonly status: 204 = 204;
  constructor(message: string) {
    throw {message, status: this.status};
  }
}
