interface IUser {
  userBusinessId?: string;
}

export function setBusinessId(...params: any[]) {
  const req = params[params.length - 2];
  const hookName = params[params.length - 1];
  const user: IUser = req.user;
  const {userBusinessId: businessId} = user ?? {};
  let businessIdUpdateIndex = 0;
  if (/patch|update|remove|get/gi.test(hookName)) {
    businessIdUpdateIndex = 1;
  }
  params[businessIdUpdateIndex].businessId = businessId;
  return;
}
